// Get all clients data from Supabase
  const clients = $input.all().map(item => item.json);

  console.log('=== GET ALL CLIENTS DEBUG ===');
  console.log('Number of clients returned:', clients.length);

  // Sort clients alphabetically by last name, then first name
  const sortedClients = clients.sort((a, b) => {
    const lastNameCompare = (a.lastname || '').localeCompare(b.lastname || '');
    if (lastNameCompare !== 0) {
      return lastNameCompare;
    }
    // If last names are the same, sort by first name
    return (a.firstname || '').localeCompare(b.firstname || '');
  });

  // Map clients data with all fields including new ones
  const formattedClients = sortedClients.map(client => ({
    ...client,
    active: client.active !== false, // Default to true if undefined/null
    mapaddress: client.mapaddress || '', // Ensure mapaddress is never null for
  frontend
    appointmentLength: client.appointment_length || null, // Map appointment_length to
  appointmentLength for frontend
    primaryClinicId: client.primary_clinic_id || null, // Map primary_clinic_id for
  frontend
    openphoneContactId: client.openphone_contact_id || null // Map openphone_contact_id
   for frontend
  }));

  console.log('Active status check:', formattedClients.slice(0, 3).map(c => ({
    knumber: c.knumber,
    active: c.active,
    mapaddress: c.mapaddress ? 'Has map address' : 'No map address',
    appointmentLength: c.appointmentLength || 'Default length',
    primaryClinicId: c.primaryClinicId ? `Clinic ID: ${c.primaryClinicId}` : 'No
  primary clinic',
    openphoneContactId: c.openphoneContactId ? 'Has OpenPhone ID' : 'No OpenPhone ID'
  })));

  return [{
    json: {
      success: true,
      clients: formattedClients,
      count: formattedClients.length,
      activeCount: formattedClients.filter(c => c.active).length,
      inactiveCount: formattedClients.filter(c => !c.active).length,
      syncedCount: formattedClients.filter(c => c.openphoneContactId).length,
      message: `Found ${formattedClients.length} clients`
    }
  }];

