// RRTS - Get Clinic Locations - Format Response
// Formats destinations data for the frontend dropdown
// v1.1.0 - Optimized logging to reduce volume

// Get the destinations data from the Supabase query
const destinationsResult = $input.all();

// Process destinations into the required format for frontend
const destinations = [];

destinationsResult.forEach((item) => {
    const destination = item.json;

    if (destination && destination.name && destination.address) {
        destinations.push({
            id: destination.id,
            name: destination.name,
            address: destination.address,
            city: destination.city || '',
            province: destination.province || 'NS',
            postalCode: destination.postal_code || '', 
            phone: destination.phone || '',
            notes: destination.notes || ''
        });
    }
});

// Sort destinations alphabetically by name for consistent dropdown ordering
destinations.sort((a, b) => a.name.localeCompare(b.name));

// Log only summary (not individual items)
if (destinations.length === 0) {
    console.log('⚠️ No clinic locations found');
}

// Return formatted response
return [{
    json: {
        success: true,
        destinations: destinations,
        count: destinations.length,
        message: `Successfully retrieved ${destinations.length} clinic locations`,
        timestamp: new Date().toISOString()
    }
}];

