// Process Drivers Data - v1.0
const drivers = $input.all().map(item => item.json);

// Sort drivers by name
const sortedDrivers = drivers.sort((a, b) => a.name.localeCompare(b.name));

return [{
  json: {
    type: 'drivers',
    data: sortedDrivers
  }
}];

// Simple Drivers Response - v1.1.0
const driversData = $input.first().json;
const drivers = driversData.data || [];

// Only log if there's an issue
if (drivers.length === 0) {
    console.log('⚠️ No drivers found');
}

// Return simple drivers list
return [{
    json: {
        success: true,
        message: 'Drivers retrieved successfully',
        totalDrivers: drivers.length,
        drivers: drivers.map(driver => ({
            id: driver.id,
            name: driver.name,
            email: driver.email,
            phone: driver.phone,
            gender: driver.gender,
            is_male: driver.is_male,
            active: driver.active,
            workload_preference: driver.workload_preference,
            workload_percentage: driver.workload_percentage,
            preferred_calendar_type: driver.preferred_calendar_type,
            google_calendar_id: driver.google_calendar_id,
            weekly_hours: driver.weekly_hours
        }))
    }
}];

