// JWT Token Validation for Backend Workflows
// Version: v1.0.0
// Use this code node at the start of any protected workflow

// Get webhook input data
const webhookData = $input.first().json;

// Extract Authorization header
const authHeader = webhookData.headers?.authorization || webhookData.headers?.Authorization;

if (!authHeader) {
  return [{
    json: {
      _route: 'unauthorized',
      success: false,
      message: 'Missing authorization header',
      statusCode: 401,
      timestamp: new Date().toISOString()
    }
  }];
}

// Check Bearer token format
if (!authHeader.startsWith('Bearer ')) {
  return [{
    json: {
      _route: 'unauthorized',
      success: false,
      message: 'Invalid authorization format. Expected "Bearer {token}"',
      statusCode: 401,
      timestamp: new Date().toISOString()
    }
  }];
}

// Extract token
const token = authHeader.substring(7); // Remove "Bearer " prefix

// JWT validation functions (matching generation code)
function simpleHash(input) {
  let hash = 0;
  for (let i = 0; i < input.length; i++) {
    const char = input.charCodeAt(i);
    hash = ((hash << 5) - hash) + char;
    hash = hash & hash;
  }
  return hash.toString(36);
}

const JWT_SECRET = "REPLACE_WITH_ENV_VAR_JWT_SECRET";

function verifyJWT(token) {
  try {
    const parts = token.split('.');
    if (parts.length !== 3) {
      return { valid: false, error: 'Invalid token format' };
    }

    const [encodedHeader, encodedPayload, receivedSignature] = parts;

    // Verify signature
    const dataToSign = `${encodedHeader}.${encodedPayload}.${JWT_SECRET}`;
    const expectedSignature = simpleHash(dataToSign);

    if (receivedSignature !== expectedSignature) {
      return { valid: false, error: 'Invalid token signature' };
    }

    // Decode payload
    const payload = JSON.parse(atob(encodedPayload));

    // Check expiration
    const now = Math.floor(Date.now() / 1000);
    if (payload.exp && payload.exp < now) {
      return { valid: false, error: 'Token expired' };
    }

    return { valid: true, payload: payload };

  } catch (error) {
    return { valid: false, error: 'Token verification failed: ' + error.message };
  }
}

// Validate the token
const validation = verifyJWT(token);

if (!validation.valid) {
  return [{
    json: {
      _route: 'unauthorized',
      success: false,
      message: validation.error || 'Invalid token',
      statusCode: 401,
      timestamp: new Date().toISOString()
    }
  }];
}

const payload = validation.payload;

// Check token type (reject LIMITED tokens unless specifically allowed)
// Note: Individual workflows can override this by setting allowLimitedToken in webhook body
const allowLimitedToken = webhookData.body?.allowLimitedToken || false;

if (payload.type === 'limited' && !allowLimitedToken) {
  return [{
    json: {
      _route: 'unauthorized',
      success: false,
      message: 'This operation requires full authentication. Please complete password change.',
      statusCode: 403,
      timestamp: new Date().toISOString()
    }
  }];
}

// Check if refresh token is being used for non-refresh operations
if (payload.type === 'refresh') {
  return [{
    json: {
      _route: 'unauthorized',
      success: false,
      message: 'Refresh tokens cannot be used for this operation',
      statusCode: 403,
      timestamp: new Date().toISOString()
    }
  }];
}

// Token is valid - return user info and original request body
return [{
  json: {
    _route: 'authorized',
    authenticated: true,
    user: {
      username: payload.sub,
      role: payload.role,
      tokenType: payload.type,
      permissions: payload.permissions || []
    },
    requestBody: webhookData.body,
    timestamp: new Date().toISOString()
  }
}];

// Format Unauthorized Error Response - Simple Version
// Version: v1.0.0

const errorData = $input.first().json;

// Return standardized error format
return [{
  json: {
    success: false,
    error: {
      message: errorData.message || 'Unauthorized access',
      statusCode: errorData.statusCode || 401
    },
    timestamp: errorData.timestamp || new Date().toISOString()
  }
}]
