// JWT Token Validation - v1.0.0
// Operations endpoints require admin/supervisor role

const webhookData = $input.first().json;
const authHeader = webhookData.headers?.authorization || webhookData.headers?.Authorization;

if (!authHeader) {
  return [{
    json: {
      _route: 'unauthorized',
      success: false,
      message: 'Missing authorization header',
      statusCode: 401,
      timestamp: new Date().toISOString()
    }
  }];
}

if (!authHeader.startsWith('Bearer ')) {
  return [{
    json: {
      _route: 'unauthorized',
      success: false,
      message: 'Invalid authorization format',
      statusCode: 401,
      timestamp: new Date().toISOString()
    }
  }];
}

const token = authHeader.substring(7);

function simpleHash(input) {
  let hash = 0;
  for (let i = 0; i < input.length; i++) {
    const char = input.charCodeAt(i);
    hash = ((hash << 5) - hash) + char;
    hash = hash & hash;
  }
  return hash.toString(36);
}

const JWT_SECRET = "***REMOVED***";

function verifyJWT(token) {
  try {
    const parts = token.split('.');
    if (parts.length !== 3) {
      return { valid: false, error: 'Invalid token format' };
    }

    const [encodedHeader, encodedPayload, receivedSignature] = parts;
    const dataToSign = `${encodedHeader}.${encodedPayload}.${JWT_SECRET}`;
    const expectedSignature = simpleHash(dataToSign);

    if (receivedSignature !== expectedSignature) {
      return { valid: false, error: 'Invalid token signature' };
    }

    const payload = JSON.parse(atob(encodedPayload));
    const now = Math.floor(Date.now() / 1000);
    if (payload.exp && payload.exp < now) {
      return { valid: false, error: 'Token expired' };
    }

    return { valid: true, payload: payload };

  } catch (error) {
    return { valid: false, error: 'Token verification failed' };
  }
}

const validation = verifyJWT(token);

if (!validation.valid) {
  return [{
    json: {
      _route: 'unauthorized',
      success: false,
      message: validation.error || 'Invalid token',
      statusCode: 401,
      timestamp: new Date().toISOString()
    }
  }];
}

const payload = validation.payload;

if (payload.type === 'limited' || payload.type === 'refresh') {
  return [{
    json: {
      _route: 'unauthorized',
      success: false,
      message: 'This operation requires full authentication',
      statusCode: 403,
      timestamp: new Date().toISOString()
    }
  }];
}

// Role check - admin and supervisor only
const allowedRoles = ['admin', 'supervisor'];
if (!allowedRoles.includes(payload.role)) {
  return [{
    json: {
      _route: 'unauthorized',
      success: false,
      message: 'Access denied. Requires admin or supervisor role.',
      statusCode: 403,
      timestamp: new Date().toISOString()
    }
  }];
}

return [{
  json: {
    _route: 'authorized',
    user: {
      id: payload.userId,
      username: payload.sub,
      role: payload.role,
      fullName: payload.fullName
    },
    timestamp: new Date().toISOString()
  }
}];

// Enrich Assignments with Names - v1.0.0
// Test DB uses 'destinations' table with 'name' column

const allItems = $input.all().map(item => item.json);

// Separate data by table structure
// Assignments have driver_id + clinic_id + is_primary
const assignments = allItems.filter(item =>
  item.driver_id !== undefined && item.clinic_id !== undefined && item.is_primary !== undefined
);

// Drivers have driver_name column
const drivers = allItems.filter(item =>
  item.driver_name !== undefined && !item.clinic_id
);

// Clinics (destinations) have 'name' column and address
const clinics = allItems.filter(item =>
  item.name !== undefined && item.address !== undefined && !item.driver_name
);

// Build lookup maps
const driverMap = new Map(drivers.map(d => [d.id, d]));
const clinicMap = new Map(clinics.map(c => [c.id, c]));

// Enrich assignments with names
const enrichedAssignments = assignments.map(a => {
  const driver = driverMap.get(a.driver_id);
  const clinic = clinicMap.get(a.clinic_id);

  return {
    id: a.id,
    driver_id: a.driver_id,
    driver_name: driver ? driver.driver_name : 'Unknown Driver',
    clinic_id: a.clinic_id,
    clinic_name: clinic ? clinic.name : 'Unknown Clinic',
    is_primary: a.is_primary,
    notes: a.notes,
    created_at: a.created_at
  };
});

return [{
  json: {
    success: true,
    data: {
      assignments: enrichedAssignments,
      drivers: drivers.map(d => ({ id: d.id, name: d.driver_name })),
      clinics: clinics.map(c => ({ id: c.id, name: c.name }))
    },
    counts: {
      assignments: enrichedAssignments.length,
      drivers: drivers.length,
      clinics: clinics.length
    },
    message: 'Driver clinic assignments loaded',
    timestamp: new Date().toISOString()
  }
}];
