// JWT Token Validation - v1.0.0
// Update driver-clinic assignments requires ADMIN ONLY

const webhookData = $input.first().json;
const authHeader = webhookData.headers?.authorization || webhookData.headers?.Authorization;

if (!authHeader) {
  return [{
    json: {
      _route: 'unauthorized',
      success: false,
      message: 'Missing authorization header',
      statusCode: 401,
      timestamp: new Date().toISOString()
    }
  }];
}

if (!authHeader.startsWith('Bearer ')) {
  return [{
    json: {
      _route: 'unauthorized',
      success: false,
      message: 'Invalid authorization format',
      statusCode: 401,
      timestamp: new Date().toISOString()
    }
  }];
}

const token = authHeader.substring(7);

function simpleHash(input) {
  let hash = 0;
  for (let i = 0; i < input.length; i++) {
    const char = input.charCodeAt(i);
    hash = ((hash << 5) - hash) + char;
    hash = hash & hash;
  }
  return hash.toString(36);
}

const JWT_SECRET = "***REMOVED***";

function verifyJWT(token) {
  try {
    const parts = token.split('.');
    if (parts.length !== 3) {
      return { valid: false, error: 'Invalid token format' };
    }

    const [encodedHeader, encodedPayload, receivedSignature] = parts;
    const dataToSign = `${encodedHeader}.${encodedPayload}.${JWT_SECRET}`;
    const expectedSignature = simpleHash(dataToSign);

    if (receivedSignature !== expectedSignature) {
      return { valid: false, error: 'Invalid token signature' };
    }

    const payload = JSON.parse(atob(encodedPayload));
    const now = Math.floor(Date.now() / 1000);
    if (payload.exp && payload.exp < now) {
      return { valid: false, error: 'Token expired' };
    }

    return { valid: true, payload: payload };

  } catch (error) {
    return { valid: false, error: 'Token verification failed' };
  }
}

const validation = verifyJWT(token);

if (!validation.valid) {
  return [{
    json: {
      _route: 'unauthorized',
      success: false,
      message: validation.error || 'Invalid token',
      statusCode: 401,
      timestamp: new Date().toISOString()
    }
  }];
}

const payload = validation.payload;

if (payload.type === 'limited' || payload.type === 'refresh') {
  return [{
    json: {
      _route: 'unauthorized',
      success: false,
      message: 'This operation requires full authentication',
      statusCode: 403,
      timestamp: new Date().toISOString()
    }
  }];
}

// ADMIN ONLY - stricter than supervisor
if (payload.role !== 'admin') {
  return [{
    json: {
      _route: 'unauthorized',
      success: false,
      message: 'Only administrators can modify driver-clinic assignments',
      statusCode: 403,
      timestamp: new Date().toISOString()
    }
  }];
}

return [{
  json: {
    _route: 'authorized',
    user: {
      id: payload.userId,
      username: payload.sub,
      role: payload.role,
      fullName: payload.fullName
    },
    requestBody: webhookData.body,
    timestamp: new Date().toISOString()
  }
}];

// Validate Update Request - v1.0.0

const authData = $input.first().json;
const body = authData.requestBody || {};

const errors = [];

// Validate driverId
const driverId = body.driverId;
if (!driverId || isNaN(parseInt(driverId))) {
  errors.push('driverId is required and must be a number');
}

// Validate assignments array
const assignments = body.assignments;
if (!Array.isArray(assignments)) {
  errors.push('assignments must be an array');
} else {
  assignments.forEach((a, index) => {
    if (!a.clinicId || isNaN(parseInt(a.clinicId))) {
      errors.push(`assignments[${index}].clinicId must be a number`);
    }
  });
}

if (errors.length > 0) {
  return [{
    json: {
      _route: 'invalid',
      success: false,
      message: 'Validation failed: ' + errors.join(', '),
      errors: errors,
      statusCode: 400,
      timestamp: new Date().toISOString()
    }
  }];
}

return [{
  json: {
    _route: 'valid',
    driverId: parseInt(driverId),
    assignments: assignments.map(a => ({
      clinicId: parseInt(a.clinicId),
      isPrimary: a.isPrimary === true,
      notes: a.notes || null
    })),
    user: authData.user,
    timestamp: new Date().toISOString()
  }
}];

// Prepare Insert Batch - v1.0.0
// Create one item per assignment for batch insert

const validData = $('Validate Request - Code').first().json;

// If no assignments, return empty to skip insert
if (!validData.assignments || validData.assignments.length === 0) {
  return [{
    json: {
      _skip_insert: true,
      driverId: validData.driverId,
      user: validData.user,
      assignmentsCount: 0
    }
  }];
}

// Create one item per assignment for batch insert
const insertItems = validData.assignments.map(a => ({
  driver_id: validData.driverId,
  clinic_id: a.clinicId,
  is_primary: a.isPrimary,
  notes: a.notes,
  created_by: validData.user.id,
  _driverId: validData.driverId,
  _user: validData.user,
  _assignmentsCount: validData.assignments.length
}));

return insertItems.map(item => ({ json: item }));

// Format Update Response - v1.0.0

const validData = $('Validate Request - Code').first().json;
const insertResults = $input.all();

return [{
  json: {
    success: true,
    data: {
      driverId: validData.driverId,
      assignmentsUpdated: insertResults.length
    },
    message: `Driver clinic assignments updated: ${insertResults.length} assignments`,
    timestamp: new Date().toISOString()
  }
}];

// Format Empty Response - v1.0.0
// When all assignments are cleared

const inputData = $input.first().json;

return [{
  json: {
    success: true,
    data: {
      driverId: inputData.driverId,
      assignmentsUpdated: 0
    },
    message: 'All driver clinic assignments cleared',
    timestamp: new Date().toISOString()
  }
}];
