// JWT Token Validation - v1.0.0
const webhookData = $input.first().json;

const authHeader =
  webhookData.headers?.authorization ||
  webhookData.headers?.Authorization;

if (!authHeader) {
  return [{
    json: {
      _route: 'unauthorized',
      success: false,
      message: 'Missing authorization header',
      statusCode: 401,
      timestamp: new Date().toISOString()
    }
  }];
}

if (!authHeader.startsWith('Bearer ')) {
  return [{
    json: {
      _route: 'unauthorized',
      success: false,
      message: 'Invalid authorization format',
      statusCode: 401,
      timestamp: new Date().toISOString()
    }
  }];
}

const token = authHeader.substring(7);

function simpleHash(input) {
  let hash = 0;
  for (let i = 0; i < input.length; i++) {
    const char = input.charCodeAt(i);
    hash = ((hash << 5) - hash) + char;
    hash = hash & hash;
  }
  return hash.toString(36);
}

const JWT_SECRET = "RRTS_JWT_SECRET_KEY_PHASE2_TEMP";

function verifyJWT(token) {
  try {
    const parts = token.split('.');
    if (parts.length !== 3) {
      return { valid: false, error: 'Invalid token format' };
    }

    const [header, payload, sig] = parts;
    const dataToSign = `${header}.${payload}.${JWT_SECRET}`;
    const expectedSig = simpleHash(dataToSign);

    if (sig !== expectedSig) {
      return { valid: false, error: 'Invalid token signature' };
    }

    const decoded = JSON.parse(atob(payload));
    const now = Math.floor(Date.now() / 1000);
    if (decoded.exp && decoded.exp < now) {
      return { valid: false, error: 'Token expired' };
    }

    return { valid: true, payload: decoded };
  } catch (error) {
    return {
      valid: false,
      error: 'Token verification failed'
    };
  }
}

const validation = verifyJWT(token);

if (!validation.valid) {
  return [{
    json: {
      _route: 'unauthorized',
      success: false,
      message: validation.error || 'Invalid token',
      statusCode: 401,
      timestamp: new Date().toISOString()
    }
  }];
}

const payload = validation.payload;

if (payload.type === 'limited') {
  return [{
    json: {
      _route: 'unauthorized',
      success: false,
      message: 'Full authentication required',
      statusCode: 403,
      timestamp: new Date().toISOString()
    }
  }];
}

if (payload.type === 'refresh') {
  return [{
    json: {
      _route: 'unauthorized',
      success: false,
      message: 'Refresh tokens cannot be used here',
      statusCode: 403,
      timestamp: new Date().toISOString()
    }
  }];
}

// RBAC: Only admin and supervisor can access destinations management
const allowedRoles = ['admin', 'supervisor'];
if (!allowedRoles.includes(payload.role)) {
  return [{
    json: {
      _route: 'unauthorized',
      success: false,
      message: 'Insufficient permissions',
      statusCode: 403,
      timestamp: new Date().toISOString()
    }
  }];
}

return [{
  json: {
    _route: 'authorized',
    authenticated: true,
    user: {
      username: payload.sub,
      role: payload.role,
      tokenType: payload.type
    },
    requestBody: webhookData.body,
    timestamp: new Date().toISOString()
  }
}];

// Version: v1.1.0 - JWT validation with RBAC role check

// Format Destinations Response - v1.0.0
const items = $input.all();

const destinations = [];

for (const item of items) {
  const d = item.json;
  if (!d || !d.id) continue;

  destinations.push({
    id: d.id,
    name: d.name || '',
    address: d.address || '',
    city: d.city || '',
    province: d.province || '',
    postal_code: d.postal_code || '',
    phone: d.phone || '',
    email: d.email || '',
    contacts: d.contacts || [],
    notes: d.notes || '',
    map_coordinates: d.map_coordinates || null,
    active: d.active !== false,
    created_at: d.created_at || null,
    updated_at: d.updated_at || null
  });
}

destinations.sort((a, b) => a.name.localeCompare(b.name));

if (destinations.length === 0) {
  console.log('No destinations found in database');
}

return [{
  json: {
    success: true,
    message: `Retrieved ${destinations.length} destinations`,
    data: destinations,
    count: destinations.length,
    timestamp: new Date().toISOString()
  }
}];

